import React,{Component} from 'react'
import styled from 'styled-components'
import Hls from 'hls.js'
import videojs from 'video.js'
import VideoPlayer from 'react-video-js-player'
const PlayerWrapper = styled.div`
    position : relative ;
`
const PlayerContent = styled.div`

`
const videoJsOptions = {
    autoplay: false,
    playbackRates: [0.5, 1, 1.25, 1.5, 2],
    width: 720,
    height: 300,
    controls: true,
    sources: [
      {
        src: 'http://localhost:3003/vod/video2.mp4',
        type: 'video/mp4',
      },
    ],
  };
export default class Vod extends Component{
    constructor(props){
        super(props);
        this._onTouchInsidePlayer=this._onTouchInsidePlayer.bind(this);
    }
    componentDidMount(){
    }
    _onTouchInsidePlayer(){
        if (this.player.paused){
            this.player.play();
        } 
        else {
            this.player.pause();
        }
    }
    render(){
        const style = {
            width : 640,
            height : 360,
            background : '#000',
        }
        return <PlayerWrapper>
            <PlayerContent>
            <div data-vjs-player>
                <video className="vjs-default-skin" {...videoJsOptions}/>
            </div>
            </PlayerContent>

        </PlayerWrapper>
    }
}
