import React,{Component} from 'react'
import styled from 'styled-components'
import Hls from 'hls.js'
const PlayerWrapper = styled.div`
    position : relative ;
`
const PlayerContent = styled.div`

`
export default class Player extends Component{
    constructor(props){
        super(props);
        this._onTouchInsidePlayer=this._onTouchInsidePlayer.bind(this);
    }
    componentDidMount(){
        const liveChannel = 'quanle'
        console.log('component did mount');
        if(Hls.isSupported() && this.player) {
            const streamURL = `http://localhost:3002/live2/${liveChannel}/index.m3u8`;
            const video = this.player;
            const hls = new Hls();
            hls.loadSource(streamURL);
            hls.attachMedia(video);
            hls.on(Hls.Events.MANIFEST_PARSED,function() {
                video.play();
            });
        }
    }
    _onTouchInsidePlayer(){
        if (this.player.paused){
            this.player.play();
        } 
        else {
            this.player.pause();
        }
    }
    render(){
        const style = {
            width : 640,
            height : 360,
            background : '#000',
        }
        return <PlayerWrapper>
            <PlayerContent>
                <video controls={true} onClick={this._onTouchInsidePlayer} style={style} ref={(player) => this.player=player} autoPlay={true}></video> 
            </PlayerContent>

        </PlayerWrapper>
    }
}
