import React,{Component} from 'react'
import styled from 'styled-components'
import Hls from 'hls.js'
const PlayerWrapper = styled.div`
    position : relative ;
`
const PlayerContent = styled.div`

`
export default class Vod extends Component{
    constructor(props){
        super(props);
        this._onTouchInsidePlayer=this._onTouchInsidePlayer.bind(this);
    }
    componentDidMount(){
    }
    _onTouchInsidePlayer(){
        if (this.player.paused){
            this.player.play();
        } 
        else {
            this.player.pause();
        }
    }
    render(){
        const style = {
            width : 640,
            height : 360,
            background : '#000',
        }
        return <PlayerWrapper>
            <PlayerContent>
                <video src={`http://localhost:3002/hls/video2.mp4`} controls={false}  style={style} ref={(player) => this.player=player} autoPlay={true} muted={true}></video> 
            </PlayerContent>

        </PlayerWrapper>
    }
}
